public class Minof3Nos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a=10;
	      int b=20;
	      int c=30;
	      if(a<b && a<c)
	    	  System.out.println("Minimum num is: "+a);
	      else if(b<a && b<c)
	    	  System.out.println("Minimum num is: "+b );
	      else
	    	  System.out.println("Minimum num is: "+c );
	}

}
