package Practice;
import java.util.*;
public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=6;
		int i;
		int j;
for(i=1;i<=n;i++) {System.out.println();
	for( j=1;j<i;j++) {
		System.out.print(" z ");
	}
}System.out.println();
for(i=n;i>=n;i--) {
	for(j=n;j>i;j--) {
		System.out.print(" z ");
	}
}System.out.println();
	}

}